var box=$("#box"),nom=$("#city");$("#selec").click(function(){$.ajax({url:"https://api.openweathermap.org/data/2.5/weather?q="+nom.val()+",fr&appid=2304ca0a4c5b96eca86deb93757adbfa",type:"GET",dataType:"json"}).done(function(e){box.html("City: "+e.name+"</br>Longitude: "+e.coord.lon+"</br>Lattitude: "+e.coord.lat+"</br>Wind: "+e.wind.speed+" Km/h </br>Humidity: "+e.main.humidity+"</br>Pressure: "+e.main.pressure+"</br>Temperature: "+(e.main.temp-273.15).toFixed(2)+" °C</br>Temperature Min: "+(e.main.temp_min-273.15).toFixed(2)+" °C</br>Temperature Max: "+(e.main.temp_max-273.15).toFixed(2)+" °C</br>")}).fail(function(e){console.log(e)}).always(function(){console.log("complete"),console.log(nom.val())})});